class AlertHub{constructor(parentElement="body"){this.alertContainer=document.createElement("div");this.alertContainer.className="alert-hub-container top-right";document.querySelector(parentElement).appendChild(this.alertContainer)}showAlert(params){const{title:title,description:description,position:position="top-right",type:type="info",timeout:timeout=1e3}=params;const alertBox=document.createElement("div");this.alertContainer.classList=`alert-hub-container ${position}`;alertBox.className=`alert-hub ${type}`;alertBox.innerHTML=`<h2>${title}</h2><p>${description}</p>`;this.alertContainer.appendChild(alertBox);setTimeout((()=>{this.alertContainer.removeChild(alertBox)}),timeout)}}