class AlertHub{constructor(parentElement="body"){this.maxLength=150;this.alertContainer=document.createElement("div");this.alertContainer.className="alert-hub-container top-right";document.querySelector(parentElement).appendChild(this.alertContainer)}showAlert(params){const{title:title,description:description,position:position="top-right",type:type="info",timeout:timeout=1e3,closeButton:closeButton=true,closeButtonSize:closeButtonSize=20,animation:animation=null}=params;const secondsTimeOut=this.timeInMilliseconds(timeout);const alertBox=this.createAlertBox(title,description,type,animation,closeButton,closeButtonSize);this.configureAlertContainer(position);this.alertContainer.appendChild(alertBox);if(closeButton){this.CloseAlertOnClick(alertBox)}this.timeoutAfter(secondsTimeOut,alertBox)}createAlertBox(title,description,type,animation,closeButton,closeButtonSize){const alertBox=document.createElement("div");alertBox.className=`alert-hub ${type} ${animation}`;const content=`\n            <div style="width: 90%;">\n                <h2>${title}</h2>\n                <p>${this.setMaxDescriptionLength(this.maxLength,description)}</p>\n            </div>\n            ${closeButton?this.createCloseButtonSvg(closeButtonSize):""}\n        `;alertBox.innerHTML=content;return alertBox}configureAlertContainer(position){this.alertContainer.className=`alert-hub-container ${position}`}setMaxDescriptionLength(length,description){if(description.length>length){description=description.substring(0,length)}return description}createCloseButtonSvg(size){return`\n            <svg class="alert-hub-close-button" height="${size}" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke-width="1.5" stroke="currentColor">\n                <path stroke-linecap="round" stroke-linejoin="round" d="M6 18L18 6M6 6l12 12" />\n            </svg>\n        `}CloseAlertOnClick(alertBox){const closeButtonElement=alertBox.querySelector(".alert-hub-close-button");closeButtonElement.addEventListener("click",(()=>{this.alertContainer.removeChild(alertBox)}))}timeoutAfter(timeout,alertBox){if(timeout<0){return}setTimeout((()=>{this.alertContainer.removeChild(alertBox)}),timeout)}timeInMilliseconds(timeInput){return timeInput*1e3}}